const includesOperator = (text) => {
  if(text.includes('+') || text.includes('-') || text.includes("*") || text.includes("/")) {
    return true;
  }

  return false;
}

export const operationEquals = (value) => {
    const operators = ['+', '-', '*', '/', '='];

    // https://developer.mozilla.org/pt-BR/docs/Web/JavaScript/Reference/Global_Objects/String/includes
    if(value.includes("+")){
      const array = value.split("+");
      let result = 0;

      array.forEach((item) => {
        result += parseInt(item);
      })

      return result.toString();
    }

    if(value.includes("-")){
      const array = value.split("-");
      let result = 0;

      array.forEach((item) => {
        result = result - parseInt(item);
      })

      return result.toString()
    }

    if(value.includes("*")){
      const array = value.split("*");
      console.log(array)
      let result = 1;

      array.forEach((item) => {
        result *= parseInt(item);
      })

      return result.toString()
    }

    return value;
  }

  export {includesOperator}
